const blogListQuery = graphql`
  query blogListQuery($skip: Int!, $limit: Int!) {
    allMarkdownRemark(
      sort: {
          order: DESC, fields: [frontmatter___date],
      }
      limit: $limit
      skip: $skip
    ) {
      pageInfo {
        hasNextPage
        currentPage
        hasPreviousPage
      }
      edges {
        node {
          id
          excerpt
          frontmatter {
            slug
            title
            date(formatString: "MMMM D, YYYY")
            cover_photo
          }
        }
      }
    }
  }
`
